import { useState, useEffect } from "react";
import { Navbar, Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function NavBar() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  const navigate = useNavigate();

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => {
      const newMode = !prevMode;
      localStorage.setItem("theme", newMode ? "dark" : "light");
      return newMode;
    });
  };

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") === "dark";
    setDarkMode(savedTheme);
  }, []);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

    const handleFavoritesButtonClick = () => {
        navigate("/favorites");
    };

  return (
    <Navbar expand="lg" style={{ backgroundColor: "#ff5733" }}>
      <Container>
        <Navbar.Brand onClick={() => navigate("/")} className="navbar-brand" style={{ cursor: "pointer" }}>
          <b>Daily Digest</b>
        </Navbar.Brand>

        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav" className="justify-content-center">

          <Button className="theme-toggle-btn" onClick={handleFavoritesButtonClick}>
            <span style={{ color: "red" }}>‚ù§</span> Favourites
          </Button>

          <Button className="theme-toggle-btn" onClick={toggleDarkMode}>
            {darkMode ? 'üåô' : '‚òÄÔ∏è'}
          </Button>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
