import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./components/NavBar";
import NewsTabs from "./components/NewsTabs";
import Banner from "./components/Banner";
import NewsSection from './components/NewsSection';

function App() {
  // Load favorites from localStorage
  const [favorites, setFavorites] = useState(() => {
    return JSON.parse(localStorage.getItem("favorites")) || [];
  });

  // Save favorites to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [favorites]);

  // Toggle favorite articles
  const toggleFavorite = (article) => {
    setFavorites((prevFavorites) => {
      const isFavorite = prevFavorites.some((fav) => fav.url === article.url);
      return isFavorite
        ? prevFavorites.filter((fav) => fav.url !== article.url) // Remove from favorites
        : [...prevFavorites, article]; // Add to favorites
    });
  };

  return (
    <Router>
      <div className="App">
        {/* Pass favorites count to NavBar */}
        <NavBar favoritesCount={favorites.length} />

        <NewsTabs />
        <Banner />

        <div className="main-content">
          <h1><b>Daily Digest</b></h1><hr />

          <Routes>
            <Route path="/" element={<NewsSection toggleFavorite={toggleFavorite} favorites={favorites} />} />
            <Route path="/favorites" element={<NewsSection favorites={favorites} toggleFavorite={toggleFavorite} isFavoritesPage={true} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
