import './App.css';
import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import NavBar from "./components/NavBar";
import NewsTabs from "./components/NewsTabs";
import Banner from "./components/Banner";
import NewsSection from './components/NewsSection';
import Connect from './components/Connect';
import Favorites from "./components/Favorites";  // ✅ Import Favorites component

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <Router>  {/* ✅ Wrap the entire app inside Router */}
      <div className="App">
        {/* Fixed Navbar */}
        <div className="navbar-container">
          <NavBar />
        </div>

        {/* Fixed NewsTabs */}
        <div className="news-tabs-container">
          <NewsTabs />
        </div>

        <Routes>
          <Route 
            path="/" 
            element={
              <>
                {/* Main Content (Push down to prevent overlap) */}
                <Banner onSearch={setSearchQuery} />  {/* Pass search function to Banner */}
                <div className="main-content">
                  <h1><b>Daily Digest</b></h1><hr />
                  <NewsSection searchQuery={searchQuery} />  {/* Pass search query to NewsSection */}
                </div>
                <Connect />
              </>
            } 
          />

          <Route path="/favorites" element={<Favorites />} />  {/* ✅ Add a route for Favorites */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
