import './App.css';
import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Routes, Route } from "react-router-dom";
import NavBar from "./components/NavBar";
import NewsTabs from "./components/NewsTabs";
import Banner from "./components/Banner";
import NewsSection from './components/NewsSection';
import Connect from './components/Connect';
import Favorites from "./components/Favorites";  // ✅ Import Favorites component

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <div className="App">
      {/* Fixed Navbar */}
      <div className="navbar-container">
        <NavBar />
      </div>

      {/* Fixed NewsTabs */}
      <div className="news-tabs-container">
        <NewsTabs />
      </div>

      <Routes>
        <Route 
          path="/" 
          element={
            <>
              {/* Main Content (Push down to prevent overlap) */}
              <Banner onSearch={setSearchQuery} />  
              <div className="main-content">
                <h1><b>Daily Digest</b></h1><hr />
                <NewsSection searchQuery={searchQuery} />  
              </div>
              <Connect />
            </>
          } 
        />

        <Route path="/favorites" element={<Favorites />} />  {/* ✅ Route for Favorites */}
      </Routes>
    </div>
  );
}

export default App;
