import { useEffect, useState } from "react";
import Nav from "react-bootstrap/Nav";
import "../App.css"; // Import CSS file

function NewsTabs() {
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("theme") === "dark"
  );

  // Listen for changes in dark mode
  useEffect(() => {
    const handleThemeChange = () => {
      setDarkMode(localStorage.getItem("theme") === "dark");
    };

    window.addEventListener("storage", handleThemeChange);
    return () => window.removeEventListener("storage", handleThemeChange);
  }, []);

  return (
    <Nav
      fill
      variant="tabs"
      defaultActiveKey="/home"
      className={darkMode ? "dark-mode-tabs" : "light-mode-tabs"}
    >
      <Nav.Item>
        <Nav.Link href="/home" className="custom-nav-link">
          General
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-1" className="custom-nav-link">
          Business
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-2" className="custom-nav-link">
          Entertainment
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-3" className="custom-nav-link">
          Health
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-4" className="custom-nav-link">
          Science
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-5" className="custom-nav-link">
          Sports
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-6" className="custom-nav-link">
          Technology
        </Nav.Link>
      </Nav.Item>
    </Nav>
  );
}

export default NewsTabs;
