import './App.css';
import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from "./components/NavBar";
import NewsTabs from "./components/NewsTabs";
import Banner from "./components/Banner";
import NewsSection from './components/NewsSection';
import Connect from './components/Connect';

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <div className="App">
      {/* Fixed Navbar */}
      <div className="navbar-container">
        <NavBar />
      </div>

      {/* Fixed NewsTabs */}
      <div className="news-tabs-container">
        <NewsTabs />
      </div>

      {/* Main Content (Push down to prevent overlap) */}
      <Banner onSearch={setSearchQuery} />  {/* Pass search function to Banner */}
      <div className="main-content">
        <h1><b>Daily Digest</b></h1><hr />
        <NewsSection searchQuery={searchQuery} />  {/* Pass search query to NewsSection */}
      </div>

      <Connect/>
    </div>
  );
}

export default App;
