import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";

const placeholderImg =
  "https://via.placeholder.com/800x400?text=No+Image+Available"; // Fallback image

function NewsCarousel() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        let response = await fetch(
          "https://newsapi.org/v2/top-headlines?country=us&pageSize=5&apiKey=REACT_APP_NEWS_API_KEY"
        );
        let data = await response.json();

        console.log("Fetched hot news:", data);

        // If hot news is empty, fetch general news as a fallback
        if (!data.articles || data.articles.length === 0) {
          console.warn("Hot news unavailable. Fetching general news...");
          response = await fetch(
            "https://newsapi.org/v2/top-headlines?country=us&category=general&pageSize=5&apiKey=YOUR_API_KEY"
          );
          data = await response.json();
        }

        console.log("Final news data:", data.articles);
        setNews(data.articles || []); // Ensure it's always an array
      } catch (error) {
        console.error("Error fetching news:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, []);

  // Show a loading message while fetching
  if (loading) {
    return <p className="loading-message">Loading news...</p>;
  }

  // If no news is found, show a message
  if (news.length === 0) {
    return <p className="loading-message">No news available at the moment.</p>;
  }

  return (
    <div className="news-carousel-container">
      <h2 className="news-carousel-title">ðŸ”¥ Hot News</h2>
      <Carousel className="news-carousel">
        {news.slice(0, 3).map((article, index) => (
          <Carousel.Item key={index} interval={5000}>
            <img
              src={article.urlToImage || placeholderImg}
              alt={article.title}
              className="d-block w-100 carousel-img"
            />
            <Carousel.Caption>
              <h3>{article.title}</h3>
              <p>{article.description || "No description available."}</p>
              <a href={article.url} target="_blank" rel="noopener noreferrer" className="news-link">
                Read More
              </a>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>
  );
}

export default NewsCarousel;
