import { useState, useEffect } from "react";
import { Navbar, Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function NavBar() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  const navigate = useNavigate();

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => {
      const newMode = !prevMode;
      localStorage.setItem("theme", newMode ? "dark" : "light");
      return newMode;
    });
  };

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  const handleFavoritesButtonClick = () => {
    navigate("/favorites");
  };

  return (
    <Navbar expand="lg" style={{ backgroundColor: "#ff5733" }}>
      <Container>
        {/* Logo on the Left */}
        <Navbar.Brand 
          onClick={() => navigate("/")} 
          className="navbar-brand" 
          style={{ cursor: "pointer" }}
        >
          <b>Daily Digest</b>
        </Navbar.Brand>

        {/* Toggle Button for Mobile View */}
        <Navbar.Toggle aria-controls="basic-navbar-nav" />

        {/* Align buttons to the Right */}
        <Navbar.Collapse id="basic-navbar-nav" className="ms-auto justify-content-end">
        <Button className="theme-toggle-btn" onClick={() => navigate("/favorites")}>
          <span style={{ color: "red" }}>‚ù§</span> Favorites
        </Button>
        
          <Button 
            className="custom-button ms-2" 
            onClick={toggleDarkMode}
          >
            {darkMode ? "üåô" : "‚òÄÔ∏è"}
          </Button>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
