import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import "./NewsCarousel.css"; // Import the CSS file

const placeholderImg =
  "https://via.placeholder.com/800x400?text=No+Image+Available"; // Default image

function NewsCarousel() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(
          "https://newsapi.org/v2/top-headlines?country=us&pageSize=5&apiKey=YOUR_API_KEY"
        );
        const data = await response.json();
        console.log("Fetched news:", data.articles); // Debugging log
        setNews(data.articles || []); // Store news (or empty array if null)
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="news-carousel-container">
      <h2 className="news-carousel-title">ðŸ”¥ Hot News</h2>
      {news.length > 0 ? (
        <Carousel className="news-carousel">
          {news.slice(0, 3).map((article, index) => (
            <Carousel.Item key={index} interval={5000}>
              <img
                src={article.urlToImage || placeholderImg}
                alt={article.title}
                className="d-block w-100 carousel-img"
              />
              <Carousel.Caption>
                <h3>{article.title}</h3>
                <p>{article.description || "No description available."}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer" className="news-link">
                  Read More
                </a>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
      ) : (
        <p className="loading-message">Loading news...</p>
      )}
    </div>
  );
}

export default NewsCarousel;
