import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Container from "react-bootstrap/Container";
import "bootstrap-icons/font/bootstrap-icons.css";
import "../css/NewsSection.css";

const NEWS_API_KEY = process.env.REACT_APP_NEWS_API_KEY;
const CATEGORIES = [
  "general",
  "business",
  "entertainment",
  "health",
  "science",
  "sports",
  "technology",
];

function NewsSection({ searchQuery }) {
  const [news, setNews] = useState({});
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem("favorites")) || []);
  const scrollRefs = useRef({});
  const navigate = useNavigate();

  // Fetch news for a category or search query
  const fetchNews = async () => {
    let apiUrl = searchQuery
      ? `https://newsapi.org/v2/everything?q=${searchQuery}&apiKey=${NEWS_API_KEY}`
      : `https://newsapi.org/v2/top-headlines?country=us&apiKey=${NEWS_API_KEY}`;

    try {
      const response = await axios.get(apiUrl);
      if (searchQuery) {
        setNews({ searchResults: response.data.articles || [] }); // Store search results separately
      } else {
        let categorizedNews = {};
        for (const category of CATEGORIES) {
          categorizedNews[category] = response.data.articles || [];
        }
        setNews(categorizedNews);
      }
    } catch (error) {
      console.error("Error fetching news:", error.message);
    }
  };

  // Fetch news on mount or when search query changes
  useEffect(() => {
    fetchNews();
  }, [searchQuery]);

  // Scroll function
  const scroll = (category, direction) => {
    const container = scrollRefs.current[category];
    if (container) {
      container.scrollBy({ left: direction * 300, behavior: "smooth" });
    }
  };

  // Toggle favorite articles
  const toggleFavorite = (article) => {
    let updatedFavorites = favorites.some((fav) => fav.url === article.url)
      ? favorites.filter((fav) => fav.url !== article.url) // Remove if already favorited
      : [...favorites, article]; // Add if not favorited

    setFavorites(updatedFavorites);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  };

  // Redirect to Favorite Page
  const goToFavorites = () => {
    navigate("/favorites");
  };

  // Share article
  const shareArticle = (article) => {
    if (navigator.share) {
      navigator
        .share({ title: article.title, url: article.url })
        .then(() => console.log("Article shared!"))
        .catch((error) => console.log("Sharing failed:", error));
    } else {
      navigator.clipboard.writeText(`${article.title} - ${article.url}`);
      alert("Link copied to clipboard!");
    }
  };

  return (
    <Container className="news-section">
      {/* Favorites Page Button */}
      <button className="favorites-page-btn" onClick={goToFavorites}>
        Go to Favorites ‚ù§Ô∏è
      </button>

      {/* Render search results if a search was made */}
      {searchQuery && (
        <div className="news-category">
          <h2>Search Results for "{searchQuery}"</h2>
          <div className="news-scroll-container">
            {news.searchResults?.map((article, index) => (
              <div key={index} className="news-card">
                <div className="news-image-container">
                  <img
                    src={article.urlToImage || "https://via.placeholder.com/300x150?text=No+Image"}
                    alt={article.title}
                    onError={(e) => {
                      e.target.src = "https://via.placeholder.com/300x150?text=No+Image";
                    }}
                  />
                </div>
                <div className="news-content">
                  <h5>{article.title}</h5>
                  <p>{article.description}</p>

                  {/* Favorite Button */}
                  <button
                    className={`favorite-btn ${favorites.some((fav) => fav.url === article.url) ? "favorited" : ""}`}
                    onClick={() => toggleFavorite(article)}
                  >
                    {favorites.some((fav) => fav.url === article.url) ? "‚ù§ Favorited" : "ü§ç Favorite"}
                  </button>

                  <a href={article.url} target="_blank" rel="noopener noreferrer">
                    Read More <i className="bi bi-arrow-right"></i>
                  </a>
                  <br />
                  <button className="share-icon" onClick={() => shareArticle(article)}>
                    <i className="bi bi-share-fill"></i>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Render categories if no search query */}
      {!searchQuery &&
        CATEGORIES.map((category) => (
          <div key={category} id={category} className="news-category">
            <h2>{category.toUpperCase()} NEWS</h2>
            <div className="scroll-wrapper">
              <button className="scroll-btn left" onClick={() => scroll(category, -1)}>
                <i className="bi bi-chevron-left"></i>
              </button>

              <div className="news-scroll-container" ref={(el) => (scrollRefs.current[category] = el)}>
                {news[category]?.map((article, index) => (
                  <div key={index} className="news-card">
                    <div className="news-image-container">
                      <img
                        src={article.urlToImage || "https://via.placeholder.com/300x150?text=No+Image"}
                        alt={article.title}
                        onError={(e) => {
                          e.target.src = "https://via.placeholder.com/300x150?text=No+Image";
                        }}
                      />
                    </div>

                    <div className="news-content">
                      <h5>{article.title}</h5>
                      <p>{article.description}</p>

                      {/* Favorite Button */}
                      <button
                        className={`favorite-btn ${favorites.some((fav) => fav.url === article.url) ? "favorited" : ""}`}
                        onClick={() => toggleFavorite(article)}
                      >
                        {favorites.some((fav) => fav.url === article.url) ? "‚ù§ Favorited" : "ü§ç Favorite"}
                      </button>

                      <a href={article.url} target="_blank" rel="noopener noreferrer">
                        Read More <i className="bi bi-arrow-right"></i>
                      </a>
                      <br />
                      <button className="share-icon" onClick={() => shareArticle(article)}>
                        <i className="bi bi-share-fill"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>

              <button className="scroll-btn right" onClick={() => scroll(category, 1)}>
                <i className="bi bi-chevron-right"></i>
              </button>
            </div>
          </div>
        ))}
    </Container>
  );
}

export default NewsSection;
