import { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";

const placeholderImg =
  "https://via.placeholder.com/800x400?text=No+Image"; // Placeholder for missing images

function NewsCarousel() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    // Fetch the top news from an API
    const fetchNews = async () => {
      try {
        const response = await fetch(
          `https://newsapi.org/v2/top-headlines?country=us&pageSize=5&apiKey=YOUR_NEWS_API_KEY`
        );
        const data = await response.json();
        setNews(data.articles.slice(0, 5)); // Get top 5 news items
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchNews();
  }, []);

  return (
    <Carousel className="news-carousel">
      {news.length > 0 ? (
        news.map((article, index) => (
          <Carousel.Item key={index} interval={5000}>
            <img
              src={article.urlToImage || placeholderImg}
              alt={article.title}
              className="d-block w-100"
              style={{ height: "400px", objectFit: "cover" }}
            />
            <Carousel.Caption>
              <h3>{article.title}</h3>
              <p>{article.description}</p>
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                Read more
              </a>
            </Carousel.Caption>
          </Carousel.Item>
        ))
      ) : (
        <Carousel.Item>
          <img src={placeholderImg} alt="Loading..." className="d-block w-100" />
          <Carousel.Caption>
            <h3>Loading news...</h3>
          </Carousel.Caption>
        </Carousel.Item>
      )}
    </Carousel>
  );
}

export default NewsCarousel;
