import './App.css';
import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import NavBar from "./components/NavBar";
import NewsTabs from "./components/NewsTabs";
import Banner from "./components/Banner";
import NewsSection from './components/NewsSection';
import Connect from './components/Connect';
import Favorites from "./components/Favorites";  // ✅ Import Favorites component

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <Routes>  {/* ✅ Ensure <Routes> is wrapping everything */}
      <Route 
        path="/" 
        element={
          <>
            <NavBar />
            <NewsTabs />
            <Banner onSearch={setSearchQuery} />
            <div className="main-content">
              <h1><b>Daily Digest</b></h1><hr />
              <NewsSection searchQuery={searchQuery} />
            </div>
            <Connect />
          </>
        } 
      />

      <Route path="/favorites" element={<Favorites />} />  {/* ✅ Ensure this route is here */}
    </Routes>
  );
}

export default App;
